import styled from "styled-components";

type ContainerProps = {
  showBackground: boolean;
}

export const Container = styled.div<ContainerProps>`
  background-color: ${props => props.showBackground === true ? '#1550FF' : ''};
`;

export const Icon = styled.img``;
