import styled from "styled-components";

type ContainerProps = {
  showBackground: boolean;
}

export const Container = styled.div<ContainerProps>`
  background-color: ${props => props.showBackground === true ? '#1550FF' : '#E2E3E3'};
  height: 100px;
  border-radius
`;

export const Icon = styled.img``;
